Methods.Code:
create object instance objednavka of Objednavka;
objednavka.naSklade = TRUE;
write("Spracovanie objednávky začalo");
if (objednavka.naSklade)
	write("Tovar je dostupný");
else
	write("Tovar nie je skladom, automaticky sa objedná od dodávateľa");
	write("Objednávka je zrušená");
return FALSE;
end if;
sposobPlatby = "Karta";
if (sposobPlatby == "Karta")
	objednavka.jeUhradena = TRUE;
elif (sposobPlatby == "Prevod")
	write("Čakanie na úhradu");
	objednavka.jeUhradena = TRUE;
end if;
if (objednavka.jeUhradena)
	objednavka.odoslatObjednavku();
else
	write("Chyba pri platbe, objednávka je zrušená");
return FALSE;
end if;
write("Objednávka bola odoslaná");
return TRUE;
